package com.ternary;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=55;
		
		
		System.out.println("Number is "+((num%2==0)?"Even":"Odd"));
		System.out.println("===============================================");
		
		
		String res=(num%2==0)?"Even":"Odd";
		System.out.println("Number is "+res);
	}

}
